{
  "query": "mutation Signup($input: SignupInput!) { signup(input: $input) { success message token user { _id username email created_at updated_at } } }",
  "variables": {
    "input": {
      "username": "carlos",
      "email": "carlos@example.com",
      "password": "Password123"
    }
  }
}

{
  "query": "query Login($usernameOrEmail: String!, $password: String!) { login(usernameOrEmail: $usernameOrEmail, password: $password) { success message token user { _id username email } } }",
  "variables": {
    "usernameOrEmail": "carlos",
    "password": "Password123"
  }
}

{
  "query": "query { getAllEmployees { success message employees { _id first_name last_name email gender designation salary date_of_joining department employee_photo } } }",
  "variables": {}
}

{
  "query": "mutation Add($input: EmployeeInput!) { addNewEmployee(input: $input) { success message employee { _id first_name last_name email designation department employee_photo } } }",
  "variables": {
    "input": {
      "first_name": "John",
      "last_name": "Doe",
      "email": "john.doe@example.com",
      "gender": "Male",
      "designation": "Developer",
      "salary": 5000,
      "date_of_joining": "2025-01-01",
      "department": "IT",
      "employee_photo": "data:image/png;base64,REPLACE_WITH_BASE64"
    }
  }
}

{
  "query": "query ($eid: ID!) { searchEmployeeByEid(eid: $eid) { success message employee { _id first_name last_name email designation department } } }",
  "variables": {
    "eid": "{{eid}}"
  }
}

{
  "query": "mutation ($eid: ID!, $input: EmployeeUpdateInput!) { updateEmployeeByEid(eid: $eid, input: $input) { success message employee { _id first_name last_name email designation department salary } } }",
  "variables": {
    "eid": "{{eid}}",
    "input": {
      "salary": 6500,
      "designation": "Senior Developer"
    }
  }
}

{
  "query": "query ($designation: String, $department: String) { searchEmployees(designation: $designation, department: $department) { success message employees { _id first_name last_name email designation department } } }",
  "variables": {
    "designation": "Developer",
    "department": ""
  }
}
